function getAll() {
    listItems = menu.map(item => {
        menuItems.innerHTML += 
        `
        <div class = 'flex-container'>
        <div class = 'img-container'>
        <img src=${item.img} alt=${item.title}>
        </div>
        <div class='menu-info'>
        <div class='menu-title'>
        <h4>${item.title}</h4>
        <h4 class='price'>${item.price}₺</h4>
        </div>
        <div class='menu-text'>${item.desc}</div>
        </div>
        </div>
        `
    });
}

